&lt;p class=&quot;MsoNoSpacing&quot; style=&quot;margin-left: 71.4pt; text-align: justify; text-indent: -18.0pt; mso-list: l2 level1 lfo4;&quot;&gt;&lt;!--[if !supportLists]--&gt;&lt;span style=&quot;font-family: Symbol; mso-fareast-font-family: Symbol; mso-bidi-font-family: Symbol;&quot; lang=&quot;ES-UY&quot;&gt;&lt;span style=&quot;mso-list: Ignore;&quot;&gt;·&lt;span style=&quot;font: 7.0pt &amp;quot;Times New Roman&amp;quot;;&quot;&gt; &lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;!--[endif]--&gt;&lt;span lang=&quot;ES-UY&quot;&gt;Análisis y optimización de procesos de comercio exterior&lt;/span&gt;&lt;/p&gt;
&lt;p class=&quot;MsoNoSpacing&quot; style=&quot;margin-left: 71.4pt; text-align: justify; text-indent: -18.0pt; mso-list: l2 level1 lfo4;&quot;&gt;&lt;!--[if !supportLists]--&gt;&lt;span style=&quot;font-family: Symbol; mso-fareast-font-family: Symbol; mso-bidi-font-family: Symbol;&quot; lang=&quot;ES-UY&quot;&gt;&lt;span style=&quot;mso-list: Ignore;&quot;&gt;·&lt;span style=&quot;font: 7.0pt &amp;quot;Times New Roman&amp;quot;;&quot;&gt; &lt;/span&gt;&lt;/span&gt;&lt;/span&gt;&lt;!--[endif]--&gt;&lt;span lang=&quot;ES-UY&quot;&gt;Apoyo en el desarrollo de nuevos negocios internacionales&lt;/span&gt;&lt;/p&gt;
